};

// PARA OBTENERLOS POR id
const getReceta = async (req, res) => {

    try {
        // console.log(req.params)
        const { id } = req.params;
        const connection = await getConnection();
        const [result] = await connection.query(
            `SELECT  
            recetas_del_mundo.id_receta, 
